dci.transformation:
errorState = "";
if ($1 == "")
	return "OK";
if ($1 & (1 << 31))
	errorState = "Paper is low, ";
if ($1 & (1 << 30))
	errorState = errorState .. "No paper, ";
if ($1 & (1 << 29))
	errorState = errorState .. "Low toner, ";
if ($1 & (1 << 28))
	errorState = errorState .. "No toner, ";
if ($1 & (1 << 27))
	errorState = errorState .. "Door open, ";
if ($1 & (1 << 26))
	errorState = errorState .. "Jammed, ";
if ($1 & (1 << 25))
	errorState = errorState .. "Ofline, ";
if ($1 & (1 << 24))
	errorState = errorState .. "Service requested, ";
if ($1 & (1 << 23))
	errorState = errorState .. "Input tray missing, ";
if ($1 & (1 << 22))
	errorState = errorState .. "Output tray missing, ";
if ($1 & (1 << 21))
	errorState = errorState .. "Marker supply missing, ";
if ($1 & (1 << 20))
	errorState = errorState .. "Output near full, ";
if ($1 & (1 << 19))
	errorState = errorState .. "Output full, ";
if ($1 & (1 << 18))
	errorState = errorState .. "Input tray empty, ";
if ($1 & (1 << 17))
	errorState = errorState .. "Overdue prevent maint, ";

if (errorState == "")
	return "OK";
else
	return errorState.substring(0, (errorState.length - 2));

transport = $node.createSNMPTransport();
if (transport != null)
{	
	type = ToolBox::SNMPGetOrWalk(transport, ".1.3.6.1.2.1.43.11.1.1.7", $dci.instance);
	if (type == null || type == 19)
		return $1;
	maxValue = ToolBox::SNMPGetOrWalk(transport, ".1.3.6.1.2.1.43.11.1.1.8", $dci.instance);
	if (maxValue == null)
		return $1;
	return $1 / maxValue * 100;
}
return $1;

