/**
 * Get table form agent ant print it using println()
 */
function printAgentTable(node, tableName)
{
	// Read table data from agent
   table = node.readAgentTable(tableName);
   if (table == null)
   {
         println("ERROR: Cannot read table from agent");
         return;
   }

   // Print column names
   for(i = 0; i < table.columnCount; i++)
         print("| " .. table.getColumnName(i).left(20));
   println("|");
   for(i = 0; i < table.columnCount; i++)
         print("+" .. "-".left(21, "-"));
   println("+");

   // Print data
   for(i = 0; i < table.rowCount; i++)
   {
         for(j = 0; j < table.columnCount; j++)
         {
                  print("| " .. table.get(i, j).left(20));
         }
         println("|");
   }
}

/**
 * Get SNMP metric as a string. Function will return obtained value or null if failed to create transport
 */
function readSNMPMetric(node, oid)
{
	transport = node.createSNMPTransport();    // Create SNMP transport for node
   if (transport == null)
   {
      println("Failed to create SNMP transport, exit");
      return null;
   }
   value = transport.getValue(oid);
   return value;
}

/**
 * rint meta information about object like class name, class hierarchy, methods and attributes
 */
function info(object)
{
	c = object.__class;
	println("");
	println(c.name);
	println("   " .. c.hierarchy.join(" :: "));
	println("   Methods:");
	for(m : c.methods)
  		println("      " .. m);
	println("   Attributes:");
	for(a : c.attributes)
   		println("      " .. a);
}

/**
 * Print all possible NXSL meta information (all classes, their methods and atgtributes, and functions)
 */
function NXSLInfo()
{
	println("==== Functions ====");
	for(f : NXSL::Functions)
   		println(f);
	println("");
	println("==== Classes ====");
	for(c : NXSL::Classes)
	{
		println("");
   		println(c.name);
   		println("   " .. c.hierarchy.join(" :: "));
   		println("   Methods:");
   		for(m : c.methods)
      		println("      " .. m);
   		println("   Attributes:");
   		for(a : c.attributes)
      		println("      " .. a);
	}
}

/**
 * Get array with all nodes under root
 */
function getAllNodes(root)
{
  nodes = %{};
  for(object : root.children)
  {
    if (classof(object) == "Node")
    {
      nodes[object.id] = object;
    }
    else if (classof(object) == "Container")
    {
      for(n : GetAllNodes(object))
      {
        if (nodes[n.id] == null)
          nodes[n.id] = n;
      }
    }
  }
  return nodes.values;
}

/**
 * Check if given node is a Windows node (node should have platform name set correctly)
 */
function isWindows(object)
{
	return classof(object) == "Node" && object.isAgent && object.platformName like "windows-*";
}

/**
 * Check if given node is a Linux node (node should have platform name set correctly)
 */
function isLinux(object)
{
	return classof(object) == "Node" && object.isAgent && object.platformName like "Linux-*";
}

/**
 * Get SNMP value by requirsting exact OID or by walking base OID and selecting correct instance
 */
function SNMPGetOrWalk(transport, baseOID, instance) 
{
	oid = baseOID .. "." .. instance;
	val = transport->getValue(oid);
	if (val != null) return val;
	vars = transport->walk(baseOID);
	foreach(var: vars) 
	{
		if (var->name->right(instance->length) == instance) 
		{
			PollerTrace("[WARN] Firmware bug in device, fallen back to SNMP walk to get value of: " . oid);
			return var->value;
		}
	}
	return null;
}
